import { Tracer, Plugin, Logger, PluginConfig, PluginInternalFiles, TracerRegistry } from '@opentelemetry/types';
/** This class represent the base to patch plugin. */
export declare abstract class BasePlugin<T> implements Plugin<T> {
    protected readonly _tracerName: string;
    protected readonly _tracerVersion?: string | undefined;
    supportedVersions?: string[];
    readonly moduleName?: string;
    readonly version?: string;
    protected readonly _basedir?: string;
    protected _moduleExports: T;
    protected _tracer: Tracer;
    protected _logger: Logger;
    protected _internalFilesExports: {
        [module: string]: unknown;
    };
    protected readonly _internalFilesList?: PluginInternalFiles;
    protected _config: PluginConfig;
    constructor(_tracerName: string, _tracerVersion?: string | undefined);
    enable(moduleExports: T, tracerRegistry: TracerRegistry, logger: Logger, config?: PluginConfig): T;
    disable(): void;
    /**
     * @TODO: To avoid circular dependencies, internal file loading functionality currently
     * lives in BasePlugin. It is not meant to work in the browser and so this logic
     * should eventually be moved somewhere else where it makes more sense.
     * https://github.com/open-telemetry/opentelemetry-js/issues/285
     */
    private _loadInternalFilesExports;
    private _loadInternalModule;
    private _requireInternalFiles;
    protected abstract patch(): T;
    protected abstract unpatch(): void;
}
